import React, { createContext, useState } from 'react';
import axios from 'axios'

export const ServicesContext = createContext();

const ServicesContextProvider = props => {
  // const [services, setServices] = useState([])
  const [services, setServices] = useState([])
  const [selectedService, setSelectedService] = useState('')

  const fetchServices = value => {
    axios.get(`http://localhost:5000/services/${value}`)
    .then(response => {
      setServiceList(response.data)
      if(selectedService === '') selectService(response.data[0])
    })
  }

  const setServiceList = services => {
    // console.log(value)
    // for (const service of services) {
    //   setServices([...services, service.title])
    // }
    value.forEach(service => {
      setServices([...services, service.title])
    })
  }

  const selectService = value => {
    setSelectedService(value)
  }

  return (
    <ServicesContext.Provider
      value={{
        services,
        fetchServices,
        selectedService,
        selectService
      }}>
      {props.children}
    </ServicesContext.Provider>
  );
}

export default ServicesContextProvider;
