import React, { createContext, useState } from 'react';
import axios from 'axios'

export const ServicesDetailsContext = createContext();

const ServicesDetailsContextProvider = props => {
  const [actions, setActions] = useState([])
  const [parts, setParts] = useState([])

  const fetchActions = value => {
    axios.get(`http://localhost:5000/actions/${value}`)
    .then(response => {
      setActions(response.data)
    })
  }

  const addAction = (selectedService, newAction) => {
    const action = {
      service_id: selectedService,
      title: newAction.name,
      price: newAction.price,
    }
    axios.post(`http://localhost:5000/actions/add`, action)
    .then(response => {
      console.log(response)
    })
  }

  const fetchParts = value => {
    axios.get(`http://localhost:5000/parts/${value}`)
    .then(response => {
      setParts(response.data)
    })
  }

  const addPart = (selectedService, newPart) => {
    const part = {
      service_id: selectedService,
      title: newPart.name,
      price: newPart.price,
    }
    axios.post(`http://localhost:5000/parts/add`, part)
    .then(response => {
      console.log(response)
    })
  }

  return (
    <ServicesDetailsContext.Provider
      value={{
        actions,
        fetchActions,
        addAction,
        parts,
        fetchParts,
        addPart
      }}>
      {props.children}
    </ServicesDetailsContext.Provider>
  );
}

export default ServicesDetailsContextProvider;
